const dislikeHandler=async e=>{e.preventDefault();const t=e.currentTarget["value"];e={dislike:t};const a=[...document.querySelectorAll(".user-card")],s=a.filter(e=>e.dataset.id===t);return s.forEach(e=>e.classList.add("liked")),setTimeout(()=>s.forEach(e=>e.classList.add("like-remove")),1e3),await fetch("/dislike-user",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)})};export default dislikeHandler;